class LightQuery{constructor(parameter){switch(this._elements=[],parameter.constructor){case Element:this._elements.push(parameter);break;case NodeList:this._elements.push(...Array.from(parameter));break;case String:try{const elements=document.querySelectorAll(parameter);this._elements.push(...Array.from(elements))}catch(error){const template=document.createElement("template");template.innerHTML=parameter,this._elements.push(...Array.from(template.content.childNodes))}break;case Function:window.addEventListener("load",()=>{Reflect.apply(parameter,null,[])});break;default:console.warn("LightQuery: Invalid parameter.")}}}const $=parameter=>new LightQuery(parameter);